import{_ as o}from"./chunks/doc-collapsed-example.CqMUHFlL.js";import{_ as t,c as a,o as i,ag as d}from"./chunks/framework.Bw-5EFTY.js";const m=JSON.parse('{"title":"Sidebar Options","description":"","frontmatter":{"order":2},"headers":[],"relativePath":"guide/options.md","filePath":"en/guide/options.md","lastUpdated":1753318909000}'),l={name:"guide/options.md"};function n(r,e,s,c,h,p){return i(),a("div",null,e[0]||(e[0]=[d('<h1 id="sidebar-options" tabindex="-1">Sidebar Options <a class="header-anchor" href="#sidebar-options" aria-label="Permalink to &quot;Sidebar Options&quot;">​</a></h1><p>This page describes all the options in the VitePress Sidebar.</p><h2 id="quick-search" tabindex="-1">@ Quick search <a class="header-anchor" href="#quick-search" aria-label="Permalink to &quot;@ Quick search&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Resolving Paths</th><th>Grouping</th></tr></thead><tbody><tr><td><a href="#documentrootpath">documentRootPath</a></td><td><a href="#collapsed">collapsed</a></td></tr><tr><td><a href="#scanstartpath">scanStartPath</a></td><td><a href="#collapsedepth">collapseDepth</a></td></tr><tr><td><a href="#resolvepath">resolvePath</a></td><td><a href="#rootgrouptext">rootGroupText</a></td></tr><tr><td><a href="#basepath">basePath</a></td><td><a href="#rootgrouplink">rootGroupLink</a></td></tr><tr><td><a href="#followsymlinks">followSymlinks</a></td><td><a href="#rootgroupcollapsed">rootGroupCollapsed</a></td></tr></tbody></table><table tabindex="0"><thead><tr><th>Getting Menu Title</th><th>Getting Menu Link</th></tr></thead><tbody><tr><td><a href="#usetitlefromfileheading">useTitleFromFileHeading</a></td><td><a href="#usefolderlinkfromsamenamesubfile">useFolderLinkFromSameNameSubFile</a></td></tr><tr><td><a href="#usetitlefromfrontmatter">useTitleFromFrontmatter</a></td><td><a href="#folderlinknotincludesfilename">folderLinkNotIncludesFileName</a></td></tr><tr><td><a href="#usefoldertitlefromindexfile">useFolderTitleFromIndexFile</a></td><td><a href="#usefolderlinkfromindexfile">useFolderLinkFromIndexFile</a></td></tr><tr><td><a href="#frontmattertitlefieldname">frontmatterTitleFieldName</a></td><td></td></tr></tbody></table><table tabindex="0"><thead><tr><th>Include/Exclude</th><th>Styling Menu Title</th></tr></thead><tbody><tr><td><a href="#excludebyglobpattern">excludeByGlobPattern</a></td><td><a href="#hyphentospace">hyphenToSpace</a></td></tr><tr><td><a href="#excludefilesbyfrontmatterfieldname">excludeFilesByFrontmatterFieldName</a></td><td><a href="#underscoretospace">underscoreToSpace</a></td></tr><tr><td><a href="#excludebyfolderdepth">excludeByFolderDepth</a></td><td><a href="#capitalizefirst">capitalizeFirst</a></td></tr><tr><td><a href="#includedotfiles">includeDotFiles</a></td><td><a href="#capitalizeeachwords">capitalizeEachWords</a></td></tr><tr><td><a href="#sortmenusbyfrontmatterdate">includeEmptyFolder</a></td><td><a href="#keepmarkdownsyntaxfromtitle">keepMarkdownSyntaxFromTitle</a></td></tr><tr><td><a href="#sortmenusbyfrontmatterdate">includeRootIndexFile</a></td><td><a href="#removeprefixafterordering">removePrefixAfterOrdering</a></td></tr><tr><td><a href="#sortmenusbyfrontmatterdate">includeFolderIndexFile</a></td><td><a href="#prefixseparator">prefixSeparator</a></td></tr></tbody></table><table tabindex="0"><thead><tr><th>Sorting</th><th>Misc</th></tr></thead><tbody><tr><td><a href="#manualsortfilenamebypriority">manualSortFileNameByPriority</a></td><td><a href="#debugprint">debugPrint</a></td></tr><tr><td><a href="#sortfolderto">sortFolderTo</a></td><td></td></tr><tr><td><a href="#sortmenusbyname">sortMenusByName</a></td><td></td></tr><tr><td><a href="#sortmenusbyfiledateprefix">sortMenusByFileDatePrefix</a></td><td></td></tr><tr><td><a href="#sortmenusbyfrontmatterorder">sortMenusByFrontmatterOrder</a></td><td></td></tr><tr><td><a href="#frontmatterorderdefaultvalue">frontmatterOrderDefaultValue</a></td><td></td></tr><tr><td><a href="#sortmenusbyfrontmatterdate">sortMenusByFrontmatterDate</a></td><td></td></tr><tr><td><a href="#sortmenusorderbydescending">sortMenusOrderByDescending</a></td><td></td></tr><tr><td><a href="#sortmenusordernumericallyfromtitle">sortMenusOrderNumericallyFromTitle</a></td><td></td></tr><tr><td><a href="#sortmenusordernumericallyfromlink">sortMenusOrderNumericallyFromLink</a></td><td></td></tr></tbody></table><h2 id="documentrootpath" tabindex="-1"><code>documentRootPath</code> <a class="header-anchor" href="#documentrootpath" aria-label="Permalink to &quot;`documentRootPath`&quot;">​</a></h2><ul><li>Type: <code>string</code></li><li>Default: <code>&#39;/&#39;</code></li></ul><p>The top-level path where documentation files are located. The default value is <code>/</code>.</p><p>This is the path where the <code>.vitepress</code> directory is located, and if the folder where the documentation is located in the project root is <code>/docs</code>, then the value of this option should be set to <code>docs</code> or <code>/docs</code>.</p><div class="language-text vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>/</span></span>\n<span class="line"><span>├─ package.json</span></span>\n<span class="line"><span>├─ src/</span></span>\n<span class="line"><span>├─ docs/        &lt;--------------- `documentRootPath` (&#39;/docs&#39;)</span></span>\n<span class="line"><span>│  ├─ .vitepress/        &lt;------ VitePress config directory</span></span>\n<span class="line"><span>│  ├─ another-directory/</span></span>\n<span class="line"><span>│  ├─ hello.md</span></span>\n<span class="line"><span>│  └─ index.md</span></span>\n<span class="line"><span>└─ ...</span></span></code></pre></div><h2 id="scanstartpath" tabindex="-1"><code>scanStartPath</code> <a class="header-anchor" href="#scanstartpath" aria-label="Permalink to &quot;`scanStartPath`&quot;">​</a></h2><ul><li>Type: <code>string|null</code></li><li>Default: <code>null</code></li></ul><p>This option is used to configure multiple sidebars. You can learn more on the <strong><a href="/advanced-usage/multiple-sidebars-how-to">Multiple sidebars</a></strong> page.</p><p>The path to the root directory to scan for document lists. Files in the path set in <code>documentRootPath</code> outside the path set in <code>scanStartPath</code> will not be scanned. It is recommended that you also set <code>documentRootPath</code> if you specify <code>scanStartPath</code> because the parent path set in <code>documentRootPath</code> should appear in the <code>link</code>.</p><p>For example, if the root path is <code>/docs</code> and the document to be scanned is <code>/docs/sub-dir/scan-me</code>, the setting would look like this:</p><ul><li><code>documentRootPath</code>: <code>/docs</code>,</li><li><code>scanStartPath</code>: <code>sub-dir/scan-me</code> (Do not include the path to <code>documentRootPath</code>.)</li></ul><h2 id="resolvepath" tabindex="-1"><code>resolvePath</code> <a class="header-anchor" href="#resolvepath" aria-label="Permalink to &quot;`resolvePath`&quot;">​</a></h2><ul><li>Type: <code>string|null</code></li><li>Default: <code>null</code></li></ul><p>This option is used to configure multiple sidebars. You can learn more on the <strong><a href="/advanced-usage/multiple-sidebars-how-to">Multiple sidebars</a></strong> page.</p><p>Enter the path to the section to display a different sidebar for each path. The path must contain <code>/</code> before it. Options without this value will be set to the root section (<code>/</code>).</p><p>e.g. <code>/</code>, <code>/path/sub-path</code>, <code>/guide/</code>...</p><h2 id="basepath" tabindex="-1"><code>basePath</code> <a class="header-anchor" href="#basepath" aria-label="Permalink to &quot;`basePath`&quot;">​</a></h2><ul><li>Type: <code>string|null</code></li><li>Default: <code>null</code></li></ul><p>This option is used to configure multiple sidebars. You can learn more on the <strong><a href="/advanced-usage/multiple-sidebars-how-to">Multiple sidebars</a></strong> page.</p><p>This option can be utilized if the path has changed due to VitePress&#39;s rewrite option. It replaces the base path in VitePress. If this value does not exist, it will use the value from <code>resolvePath</code> instead.</p><h2 id="followsymlinks" tabindex="-1"><code>followSymLinks</code> <a class="header-anchor" href="#followsymlinks" aria-label="Permalink to &quot;`followSymLinks`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If the value is <code>true</code>, when scanning a directories, include any directories or files that have symbolic links set to them and add them to the menu. Be careful if you configure symbolic links incorrectly or have a complex level of links set up, as this can result in poor performance or infinite scans.</p><h2 id="usetitlefromfileheading" tabindex="-1"><code>useTitleFromFileHeading</code> <a class="header-anchor" href="#usetitlefromfileheading" aria-label="Permalink to &quot;`useTitleFromFileHeading`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If the value is <code>true</code>, display the title with the <code>h1</code> heading content of the <code>.md</code> file. If the <code>h1</code> heading does not exist in the file, it displays <code>Unknown</code>.</p><p>The default menu items are sorted in folder tree order, so set the <code>sortMenusByName</code> option to <code>true</code> if you want to re-sort by the changed menu name.</p><h2 id="usetitlefromfrontmatter" tabindex="-1"><code>useTitleFromFrontmatter</code> <a class="header-anchor" href="#usetitlefromfrontmatter" aria-label="Permalink to &quot;`useTitleFromFrontmatter`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If the value is <code>true</code>, display the title based on the value of <code>title</code> in <code>Frontmatter</code> in the file. If this value cannot be parsed, it will be taken from the <code>h1</code> tag if the <code>useTitleFromFileHeading</code> option is <code>true</code>, and from the filename if that fails.</p><p>The <code>Frontmatter</code> should be located at the top of the document, and should look like this (Space is required between the <code>title:</code> value and the title.)</p><div class="language-markdown vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">markdown</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#005CC5;--shiki-light-font-weight:bold;--shiki-dark:#79B8FF;--shiki-dark-font-weight:bold;">---</span></span>\n<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">title: Hello World</span></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-light-font-weight:bold;--shiki-dark:#79B8FF;--shiki-dark-font-weight:bold;">---</span></span></code></pre></div><h2 id="frontmattertitlefieldname" tabindex="-1"><code>frontmatterTitleFieldName</code> <a class="header-anchor" href="#frontmattertitlefieldname" aria-label="Permalink to &quot;`frontmatterTitleFieldName`&quot;">​</a></h2><ul><li>Type: <code>string</code></li><li>Default: <code>title</code></li></ul><p>Displays the menu title based on the key name in Frontmatter specified in the file. If the specified value does not exist in Frontmatter, the default <code>title</code> will be used as a fallback.</p><div class="language-markdown vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">markdown</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#005CC5;--shiki-light-font-weight:bold;--shiki-dark:#79B8FF;--shiki-dark-font-weight:bold;">---</span></span>\n<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">name: This is frontmatter title value.</span></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-light-font-weight:bold;--shiki-dark:#79B8FF;--shiki-dark-font-weight:bold;">---</span></span></code></pre></div><p>For more information, see the following articles: <a href="https://vitepress.dev/guide/frontmatter" target="_blank" rel="noreferrer">https://vitepress.dev/guide/frontmatter</a></p><p>The default menu items are sorted in folder tree order, so set the <code>sortMenusByName</code> option to <code>true</code> if you want to re-sort by the changed menu name.</p><h2 id="usefoldertitlefromindexfile" tabindex="-1"><code>useFolderTitleFromIndexFile</code> <a class="header-anchor" href="#usefoldertitlefromindexfile" aria-label="Permalink to &quot;`useFolderTitleFromIndexFile`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If this value is <code>true</code>, use the information in the current folder&#39;s <code>index.md</code> file to get the menu name. If the <code>index.md</code> file does not exist, the folder name is used. Since we typically get the name <code>index</code> from the <code>index.md</code> file, we recommend using the <code>useTitleFromFileHeading</code> or <code>useTitleFromFrontmatter</code> options together to get the title from the Markdown header or Frontmatter of that file.</p><p>The <code>index.md</code> file is hidden from the sidebar menu, but the index file can be shown in the menu if the <code>includeFolderIndexFile</code> option is <code>true</code>.</p><h2 id="usefolderlinkfromindexfile" tabindex="-1"><code>useFolderLinkFromIndexFile</code> <a class="header-anchor" href="#usefolderlinkfromindexfile" aria-label="Permalink to &quot;`useFolderLinkFromIndexFile`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If this value is <code>true</code>, specifies a link to the folder so that you can navigate to the <code>index.md</code> file in the current folder. If the <code>index.md</code> file does not exist, no link is created.</p><p>The <code>index.md</code> file is hidden from the sidebar menu, but the index file can be shown in the menu if the <code>includeFolderIndexFile</code> option is <code>true</code>.</p><h2 id="manualsortfilenamebypriority" tabindex="-1"><code>manualSortFileNameByPriority</code> <a class="header-anchor" href="#manualsortfilenamebypriority" aria-label="Permalink to &quot;`manualSortFileNameByPriority`&quot;">​</a></h2><ul><li>Type: <code>Array&lt;string&gt;</code></li><li>Default: <code>[]</code></li></ul><p>Sort by an array of file names (including extensions) in order. If there is no value in the array that matches the filename, the sort priority is sent back. This applies to both files and directories, and the same arrangement rules apply to subdirectories as well.</p><h2 id="sortfolderto" tabindex="-1"><code>sortFolderTo</code> <a class="header-anchor" href="#sortfolderto" aria-label="Permalink to &quot;`sortFolderTo`&quot;">​</a></h2><ul><li>Type: <code>undefined | &#39;top&#39; | &#39;bottom&#39;</code></li><li>Default: <code>undefined</code></li></ul><p>After all the sorting is done, folders and files are placed in batches. If the value is <code>top</code>, all folders are placed above the files, and if <code>bottom</code>, they are placed below the files. Items in subfolders are also sorted together.</p><h2 id="sortmenusbyname" tabindex="-1"><code>sortMenusByName</code> <a class="header-anchor" href="#sortmenusbyname" aria-label="Permalink to &quot;`sortMenusByName`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>Sort the items in the menu item by name. Normally, folder scans are done with an ascending name sort, so the default sort is applied without this option applied, but if you use the <code>useTitleFromFileHeading</code> or <code>useTitleFromFrontmatter</code> options, you may need to re-sort by name because the menu name changes. This option forces sorting by name even for changed menu names.</p><h2 id="sortmenusbyfiledateprefix" tabindex="-1"><code>sortMenusByFileDatePrefix</code> <a class="header-anchor" href="#sortmenusbyfiledateprefix" aria-label="Permalink to &quot;`sortMenusByFileDatePrefix`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If the value is <code>true</code>, sorts by date prefix in the name of the menu item. The date format must be in the form <code>YYYY-MM-DD</code> (e.g. <code>2024-01-01-menu-name</code>, <code>2024-01-02.menu-name</code>...)</p><p>To remove date prefixes that remain in the menu text afterward, you can utilize the <code>prefixSeparator</code> and <code>removePrefixAfterOrdering</code> options.</p><p>The default menu items are sorted in folder tree order, so set the <code>sortMenusByName</code> option to <code>true</code> if you want to re-sort by the changed menu name.</p><h2 id="sortmenusbyfrontmatterorder" tabindex="-1"><code>sortMenusByFrontmatterOrder</code> <a class="header-anchor" href="#sortmenusbyfrontmatterorder" aria-label="Permalink to &quot;`sortMenusByFrontmatterOrder`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>Sorts the menu items by the <code>order</code> property of the frontmatter. For each folder, sorts the value (number) of the <code>order</code> property in ascending order, or descending order if the <code>sortMenusOrderByDescending</code> option is <code>true</code>. If the value of <code>order</code> is non-numeric or does not exist, <code>order</code> is judged to be <code>0</code>.</p><h2 id="sortmenusbyfrontmatterdate" tabindex="-1"><code>sortMenusByFrontmatterDate</code> <a class="header-anchor" href="#sortmenusbyfrontmatterdate" aria-label="Permalink to &quot;`sortMenusByFrontmatterDate`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>Sorts the menu items by the <code>date</code> property of the frontmatter. It also sorts the <code>date</code> property values in ascending order by oldest date (or descending order if the <code>sortMenusOrderByDescending</code> option is <code>true</code>) The date format must match <code>YYYY-MM-DD</code> or the JavaScript Date data type.</p><h2 id="sortmenusorderbydescending" tabindex="-1"><code>sortMenusOrderByDescending</code> <a class="header-anchor" href="#sortmenusorderbydescending" aria-label="Permalink to &quot;`sortMenusOrderByDescending`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If this value is <code>true</code>, sorts the items in the menu item in descending order. This option is only enabled when <code>sortMenusByName</code> or <code>sortMenusByFrontmatterOrder</code> is <code>true</code>.</p><h2 id="sortmenusordernumericallyfromtitle" tabindex="-1"><code>sortMenusOrderNumericallyFromTitle</code> <a class="header-anchor" href="#sortmenusordernumericallyfromtitle" aria-label="Permalink to &quot;`sortMenusOrderNumericallyFromTitle`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If this value is <code>true</code>, If a menu name contains a number at the beginning, it is sorted by the lower number, not the name. For example, if you have files named <code>1-a</code>, <code>10-a</code>, and <code>2-a</code>, a normal sort would sort by name, <code>[&#39;1-a&#39;, &#39;10-a&#39;, &#39;2-a&#39;]</code>. This causes the menu to display in an unintended order because <code>10-a</code> takes precedence over <code>2-a</code>.</p><p>With this option, they are sorted as follows: <code>[&#39;1-a&#39;, &#39;2-a&#39;, &#39;10-a&#39;]</code></p><p>It should be used with the <code>sortMenusOrderByDescending</code> option if you want a descending sort.</p><h2 id="sortmenusordernumericallyfromlink" tabindex="-1"><code>sortMenusOrderNumericallyFromLink</code> <a class="header-anchor" href="#sortmenusordernumericallyfromlink" aria-label="Permalink to &quot;`sortMenusOrderNumericallyFromLink`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If this value is <code>true</code>, If a menu name contains a number at the beginning, it is sorted by the lower number, not the name. This option is the same as <code>sortMenusOrderNumericallyFromTitle</code>, but sorts by links instead of file titles. Therefore, it cannot be used with the <code>sortMenusOrderNumericallyFromTitle</code> option.</p><p>It should be used with the <code>sortMenusOrderByDescending</code> option if you want a descending sort.</p><h2 id="frontmatterorderdefaultvalue" tabindex="-1"><code>frontmatterOrderDefaultValue</code> <a class="header-anchor" href="#frontmatterorderdefaultvalue" aria-label="Permalink to &quot;`frontmatterOrderDefaultValue`&quot;">​</a></h2><ul><li>Type: <code>number</code></li><li>Default: <code>0</code></li></ul><p>Sets the default value for the <code>order</code> property of the frontmatter when not set. This option is only enabled when <code>sortMenusByFrontmatterOrder</code> is <code>true</code>.</p><h2 id="collapsed" tabindex="-1"><code>collapsed</code> <a class="header-anchor" href="#collapsed" aria-label="Permalink to &quot;`collapsed`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If the <code>collapsed</code> option is not specified(<code>null</code> or <code>undefined</code>), group collapse/expand is not used and all menus are displayed at once. If <code>false</code>, the menu is created with all groups expanded. If <code>true</code>, the menu is created with all groups collapsed.</p><p>(Even if the value is <code>true</code>, the menu may be expanded if it is located in a document within a collapsed group.)</p><p><img src="'+o+'" alt="Collapsed Example"></p><h2 id="collapsedepth" tabindex="-1"><code>collapseDepth</code> <a class="header-anchor" href="#collapsedepth" aria-label="Permalink to &quot;`collapseDepth`&quot;">​</a></h2><ul><li>Type: <code>number</code></li><li>Default: <code>1</code></li></ul><p>At the specified depth, the menu group is made collapsed. When this option is specified, group collapsing/expanding is automatically enabled. The depth of the top-level folder is <code>1</code>.</p><h2 id="hyphentospace" tabindex="-1"><code>hyphenToSpace</code> <a class="header-anchor" href="#hyphentospace" aria-label="Permalink to &quot;`hyphenToSpace`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If the value is <code>true</code>, the <code>-</code> symbol included in the file name is converted to a space and displayed as a title. This option is also affected when the menu name is imported via a MarkDown heading or frontmatter.</p><h2 id="underscoretospace" tabindex="-1"><code>underscoreToSpace</code> <a class="header-anchor" href="#underscoretospace" aria-label="Permalink to &quot;`underscoreToSpace`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If the value is <code>true</code>, the <code>_</code> symbol included in the file name is converted to a space and displayed as a title. This option is also affected when the menu name is imported via a MarkDown heading or frontmatter.</p><h2 id="capitalizefirst" tabindex="-1"><code>capitalizeFirst</code> <a class="header-anchor" href="#capitalizefirst" aria-label="Permalink to &quot;`capitalizeFirst`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If the value is <code>true</code>, the first letter of the menu name is forced to uppercase. This option is also affected when the menu name is imported via a MarkDown heading or frontmatter.</p><h2 id="capitalizeeachwords" tabindex="-1"><code>capitalizeEachWords</code> <a class="header-anchor" href="#capitalizeeachwords" aria-label="Permalink to &quot;`capitalizeEachWords`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If the value is <code>true</code>, will capitalize all first letters of words separated by special characters. This option is also affected when menu names are imported via markdown headers or Frontmatter.</p><p>For example, <code>abc def ghi</code> and <code>abc-def ghi</code> change to <code>Abc Def Ghi</code> and <code>Abc-Def Ghi</code>, respectively.</p><h2 id="excludebyglobpattern" tabindex="-1"><code>excludeByGlobPattern</code> <a class="header-anchor" href="#excludebyglobpattern" aria-label="Permalink to &quot;`excludeByGlobPattern`&quot;">​</a></h2><ul><li>Type: <code>Array&lt;string&gt;</code></li><li>Default: <code>[]</code></li></ul><p><a href="https://en.wikipedia.org/wiki/Glob_(programming)" target="_blank" rel="noreferrer">glob</a> Exclude files or folders based on an array of file pattern strings.</p><p>For example, the value might look like this: <code>[&#39;abc/&#39;, &#39;def.md&#39;, &#39;ghi/file-**&#39;]</code> This would exclude the <code>abc</code> directory and subdirectories in all paths, the <code>def.md</code> file, and files starting with <code>file-</code> in the <code>ghi</code> path, respectively, and these files and folders would be excluded from the menu.</p><h2 id="excludefilesbyfrontmatterfieldname" tabindex="-1"><code>excludeFilesByFrontmatterFieldName</code> <a class="header-anchor" href="#excludefilesbyfrontmatterfieldname" aria-label="Permalink to &quot;`excludeFilesByFrontmatterFieldName`&quot;">​</a></h2><ul><li>Type: <code>string|null</code></li><li>Default: <code>null</code></li></ul><p>Documents with the value of the specified frontmatter field name set to <code>true</code> are excluded from the menu.</p><p>If no option is specified or the option value is undefined, it is ignored.</p><p>For example, if the option value is <code>exclude</code>, documents whose content contains <code>exclude: true</code> are not displayed in the menu.</p><div class="language-markdown vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">markdown</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#005CC5;--shiki-light-font-weight:bold;--shiki-dark:#79B8FF;--shiki-dark-font-weight:bold;">---</span></span>\n<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">title: This article is excluded.</span></span>\n<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">exclude: true</span></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-light-font-weight:bold;--shiki-dark:#79B8FF;--shiki-dark-font-weight:bold;">---</span></span>\n<span class="line"></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-light-font-weight:bold;--shiki-dark:#79B8FF;--shiki-dark-font-weight:bold;"># Article</span></span>\n<span class="line"></span>\n<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">Content</span></span></code></pre></div><p>Depending on the value of this option, you can use other names like <code>draft</code>, <code>hide</code>, etc. instead of <code>exclude</code>.</p><h2 id="excludebyfolderdepth" tabindex="-1"><code>excludeByFolderDepth</code> <a class="header-anchor" href="#excludebyfolderdepth" aria-label="Permalink to &quot;`excludeByFolderDepth`&quot;">​</a></h2><ul><li>Type: <code>number|null</code></li><li>Default: <code>null</code></li></ul><p>When scanning a folder, when it reaches the specified number of depths, it no longer scans subfolders and files and does not display them in the menu. The topmost level is <code>1</code>.</p><p>For example, in the structure below, if the option value is <code>3</code>, the menu will be suppressed starting at the third depth.</p><div class="language-text vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>root/  &lt;---------- depth: 1 / scan: yes</span></span>\n<span class="line"><span>├─ aaa1/  &lt;---------- depth: 1</span></span>\n<span class="line"><span>│  ├─ bbb/  &lt;---------- depth: 2</span></span>\n<span class="line"><span>│  │  ├─ b1.md  &lt;---------- depth: 3 / scan: no</span></span>\n<span class="line"><span>│  │  ├─ ccc/  &lt;---------- depth: 3</span></span>\n<span class="line"><span>│  │  │  └─ c1.md  &lt;---------- depth: 4 / scan: no</span></span>\n<span class="line"><span>│  │  └─ b1.md  &lt;---------- depth: 3 / scan: no</span></span>\n<span class="line"><span>│  └─ a1.md  &lt;---------- depth: 2 / scan: yes</span></span>\n<span class="line"><span>└─ aaa2/  &lt;---------- depth: 1</span></span>\n<span class="line"><span>   └─ aaa1.md  &lt;---------- depth: 2 / scan: yes</span></span></code></pre></div><h2 id="includedotfiles" tabindex="-1"><code>includeDotFiles</code> <a class="header-anchor" href="#includedotfiles" aria-label="Permalink to &quot;`includeDotFiles`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>Normally, if file and folder names contain a dot (<code>.</code>) in front of them, they are considered hidden and not shown in the list. However, if this option is <code>true</code>, it forces all hidden files and folders to be shown in the list.</p><h2 id="includeemptyfolder" tabindex="-1"><code>includeEmptyFolder</code> <a class="header-anchor" href="#includeemptyfolder" aria-label="Permalink to &quot;`includeEmptyFolder`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If the value is <code>true</code>, also displays directories where no md file exists as a group.</p><h2 id="includerootindexfile" tabindex="-1"><code>includeRootIndexFile</code> <a class="header-anchor" href="#includerootindexfile" aria-label="Permalink to &quot;`includeRootIndexFile`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If the value is <code>true</code>, also include the top-level path <code>index.md</code> file in the sidebar menu. Use the <code>includeFolderIndexFile</code> option to include the index file of the child items as well. (If the file does not exist, it is ignored.)</p><h2 id="includefolderindexfile" tabindex="-1"><code>includeFolderIndexFile</code> <a class="header-anchor" href="#includefolderindexfile" aria-label="Permalink to &quot;`includeFolderIndexFile`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If the value is <code>true</code>, also include the folder path <code>index.md</code> file in the sidebar menu. Use the <code>includeRootIndexFile</code> option to include the index file of the root item as well. (If the file does not exist, it is ignored.)</p><h2 id="removeprefixafterordering" tabindex="-1"><code>removePrefixAfterOrdering</code> <a class="header-anchor" href="#removeprefixafterordering" aria-label="Permalink to &quot;`removePrefixAfterOrdering`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>Removes a specific prefix from each menu title from the menu items that appear after everything is done. This is ideal if you want to sort by the number in the filename without using frontmatter&#39;s sorting, and you don&#39;t want that number to be visible in the menu.</p><p>For example, if <code>prefixSeparator</code> is the default (<code>.</code>), the following menus will be renamed as follows:</p><ul><li>File name: <code>1.hello</code> -&gt; Menu name: <code>hello</code></li><li>File name: <code>1.1.hello</code> -&gt; Menu name: <code>1.hello</code></li><li>File name: <code>1-1.hello</code> -&gt; Menu name: <code>hello</code></li></ul><p>Removes letters only once based on the separator, so a child item like <code>1.1.</code> should be used like <code>1-1.</code>. Alternatively, you can set a regular expression on the <code>prefixSeparator</code> value to work around it.</p><p>Can be used with the <code>prefixSeparator</code> option. See that option&#39;s description for more information.</p><p>(Note A: prefix only affects the title, the link will use the file link as it is).</p><p>(Note B: This option is ignored if you use the <code>useTitleFromFileHeading</code> or <code>useTitleFromFrontmatter</code> options).</p><h2 id="prefixseparator" tabindex="-1"><code>prefixSeparator</code> <a class="header-anchor" href="#prefixseparator" aria-label="Permalink to &quot;`prefixSeparator`&quot;">​</a></h2><ul><li>Type: <code>string|RegExp</code></li><li>Default: <code>&#39;.&#39;</code></li></ul><p>This option can only be used in conjunction with the <code>removePrefixAfterOrdering</code> option to remove the prefix.</p><p>Removes the first part of a specified number of characters (at least one) from the extracted menu text. For example, if the menu name is <code>1. Text</code>, and you set the <code>prefixSeparator</code> value to <code>. </code>, the result will be just <code>Text</code>.</p><p>You can also use regular expressions. Values matching the regular expression are removed. For example, to remove the date before the string in <code>2024-01-01-hello</code>, specify the <code>prefixSeparator</code> value as <code>/[0-9]{4}-[0-9]{2}-[0-9]{2}-/g</code>. The result is <code>hello</code>.</p><h2 id="rootgrouptext" tabindex="-1"><code>rootGroupText</code> <a class="header-anchor" href="#rootgrouptext" aria-label="Permalink to &quot;`rootGroupText`&quot;">​</a></h2><ul><li>Type: <code>string</code></li><li>Default: <code>&#39;Table of Contents&#39;</code></li></ul><p>rootGroup specifies the entire group for the menu, regardless of directory structure. This uses one menu step, so you should be careful about using it, and you can disable the rootGroup option if you don&#39;t need it. If you specify this value, you specify a name for the top-level menu.</p><h2 id="rootgrouplink" tabindex="-1"><code>rootGroupLink</code> <a class="header-anchor" href="#rootgrouplink" aria-label="Permalink to &quot;`rootGroupLink`&quot;">​</a></h2><ul><li>Type: <code>string</code></li><li>Default: <code>null</code></li></ul><p>For more information about rootGroup, see the <code>rootGroupText</code> option description. Specifying this value specifies a link to the rootGroup. If the value is empty, no link is added.</p><h2 id="rootgroupcollapsed" tabindex="-1"><code>rootGroupCollapsed</code> <a class="header-anchor" href="#rootgroupcollapsed" aria-label="Permalink to &quot;`rootGroupCollapsed`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>null</code></li></ul><p>For more information about rootGroup, see the <code>rootGroupText</code> option description. The <code>rootGroupCollapsed</code> option sets whether child items of the root group are expanded or not. If specified with the default value of <code>null</code> or <code>undefined</code>, the expand/collapse button is not displayed. If the value is <code>true</code>, the child items are displayed collapsed, and if <code>false</code>, they are expanded.</p><p>This option only applies to top-level item. For general item collapsibility, see the <code>collapsed</code> option.</p><h2 id="usefolderlinkfromsamenamesubfile" tabindex="-1"><code>useFolderLinkFromSameNameSubFile</code> <a class="header-anchor" href="#usefolderlinkfromsamenamesubfile" aria-label="Permalink to &quot;`useFolderLinkFromSameNameSubFile`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If this value is <code>true</code>, then if a subfile with the same name as the folder exists, a link will be created in the folder to navigate to that file, and the file will not be displayed in the child item.</p><p>For example, if you have a folder that looks like this:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>docs/</span></span>\n<span class="line"><span>├─ guide/</span></span>\n<span class="line"><span>│  ├─ api/</span></span>\n<span class="line"><span>│  │  └─ api.md</span></span>\n<span class="line"><span>│  ├─ one.md</span></span>\n<span class="line"><span>│  └─ two.md</span></span>\n<span class="line"><span>└─ config/</span></span>\n<span class="line"><span>   └─ index.md</span></span></code></pre></div><p>A link is added to the <code>api</code> folder, and the <code>api</code> page in the <code>api</code> folder is not included in the menu listing. Clicking the link in the folder displays the file in <code>api/api.md</code>.</p><h2 id="folderlinknotincludesfilename" tabindex="-1"><code>folderLinkNotIncludesFileName</code> <a class="header-anchor" href="#folderlinknotincludesfilename" aria-label="Permalink to &quot;`folderLinkNotIncludesFileName`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>This option is only used in special cases: when you have a rewrite rule and a subfile with the same folder name exists, use it in parallel with the <code>useFolderLinkFromSameNameSubFile</code> option.</p><p>If this value is <code>true</code>, when establishing a folder link, ignore the existence of child items and specify the link only as a folder path.</p><p>For example, if you have a folder that looks like this:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>docs/</span></span>\n<span class="line"><span>├─ guide/</span></span>\n<span class="line"><span>│  ├─ api/</span></span>\n<span class="line"><span>│  │  └─ api.md</span></span>\n<span class="line"><span>│  ├─ one.md</span></span>\n<span class="line"><span>│  └─ two.md</span></span>\n<span class="line"><span>└─ config/</span></span>\n<span class="line"><span>   └─ index.md</span></span></code></pre></div><p>With the <code>useFolderLinkFromSameNameSubFile</code> option, clicking on the guide/api folder menu will take you to <code>guide/api/api</code>, but if you use the <code>folderLinkNotIncludesFileName</code> option with it, the link will be <code>guide/api/</code>.</p><h2 id="keepmarkdownsyntaxfromtitle" tabindex="-1"><code>keepMarkdownSyntaxFromTitle</code> <a class="header-anchor" href="#keepmarkdownsyntaxfromtitle" aria-label="Permalink to &quot;`keepMarkdownSyntaxFromTitle`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If this value is <code>true</code>, preserves the Markdown syntax contained in the title text without removing it. Usually retains any highlighting or inline code. Hyperlink text is removed regardless of this option.</p><h2 id="debugprint" tabindex="-1"><code>debugPrint</code> <a class="header-anchor" href="#debugprint" aria-label="Permalink to &quot;`debugPrint`&quot;">​</a></h2><ul><li>Type: <code>boolean</code></li><li>Default: <code>false</code></li></ul><p>If this value is <code>true</code>, prints the objects created after execution to the console log. If you configured Multiple sidebars, it will output all sidebar results even if you only include one of the options.</p>',180)]))}const b=t(l,[["render",n]]);export{m as __pageData,b as default};
